// Payment method mapping
const paymentMethods = {
  'cards': 'card',
  'mercadopago': 'mercadopago',
  'oxxo': 'oxxo',
  'spei': 'spei'
};

// Global variables
let selectedMethod = null;
let amountInput = null;

// Initialize when DOM is loaded
document.addEventListener('DOMContentLoaded', function() {
  // Get references to elements
  amountInput = document.getElementById('amountInput');

  // Add event listeners to all deposit buttons
  const depositButtons = document.querySelectorAll('.deposit-method-btn');

  depositButtons.forEach((button, index) => {
    button.addEventListener('click', function() {
      handleDepositClick(index, button);
    });
  });
});

// Handle deposit button click
async function handleDepositClick(methodIndex, payBtn) {
  // Determine which payment method was selected
  const methods = ['cards', 'mercadopago', 'oxxo', 'spei'];
  selectedMethod = paymentMethods[methods[methodIndex]];

  console.log('Selected payment method:', selectedMethod);
  console.log('Amount entered:', amountInput.value);

  // Validation
  if (!selectedMethod || !amountInput.value) {
    return alert('Select APM and amount');
  }

  const amount = parseFloat(amountInput.value);

  // Validate amount is a positive number
  if (isNaN(amount) || amount <= 0) {
    return alert('Please enter a valid amount');
  }

  // Create payment data with updated amount
  const data = {
    ...checkoutData,
    cart: {
      ...checkoutData.cart,
      total: amount,
      items: [{ ...checkoutData.cart.items[0], amount_total: amount }]
    },
    payment_method: selectedMethod,
  };

  try {
    payBtn.textContent = "Processing...";
    payBtn.disabled = true;

    // Note: Replace 'lite' with your actual Tonder Lite instance
    // const res = await lite.payment(data);

    // For demo purposes, we'll simulate the payment
    console.log("Payment data:", data);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 2000));

    console.log("Payment response: Success");
    alert("Payment successful");

    // Close modal after successful payment
    document.getElementById('depositModal').style.display = 'none';

  } catch (err) {
    console.error('Payment error:', err);
    alert("Payment failed");
  } finally {
    payBtn.textContent = "DEPOSITAR";
    payBtn.disabled = false;
  }
}

// Utility function to validate amount based on payment method limits
function validateAmount(method, amount) {
  const limits = {
    'cards': { min: 20, max: 300000 },
    'mercadopago': { min: 20, max: 10000 },
    'oxxo': { min: 20, max: 10000 },
    'spei': { min: 20, max: 100000 }
  };

  const methodLimits = limits[method];
  if (!methodLimits) return false;

  return amount >= methodLimits.min && amount <= methodLimits.max;
}
